svg.script:
    var angle_seconds   = 0;
    var angle_gear7_5   = 0;
    var angle_minutes   = 0;
    var angle_gear180   = 0;
    var angle_hours     = 0;
    var angle_gear2880  = 0;
    var angle_daynight  = 0;
    var angle_gear5760  = 0;
    var angle_daymonth  = 0; // Day of month
    var angle_gear11520 = 0;
    var angle_weekday   = 0;
    var angle_gear172800= 0;
    var angle_month     = 0;

    // Keep track of old time... so we don't update the display if not necessary.  
    // This is a mechanical clock, so it is correct to move the second hand
    // only once a second! One could add a pendulum and rocker... but that
    // requires too much CPU.
    var old_time = 0;

    // The gear groups
    var seconds;
    var gear7_5;
    var minutes;
    var gear180;
    var hours;
    var gear360;
    var daynight
    var gear5760;
    var date;
    var gear11520;
    var weekday;
    var gear172800;
    var month;

    function Start(evt) {
      seconds         = evt.target.ownerDocument.getElementById("RotSeconds"      );
      gear7_5         = evt.target.ownerDocument.getElementById("RotGear7_5"      );
      minutes         = evt.target.ownerDocument.getElementById("RotMinutes"      );
      gear180         = evt.target.ownerDocument.getElementById("RotGear180"      );
      hours           = evt.target.ownerDocument.getElementById("RotHours"        );
      gear360         = evt.target.ownerDocument.getElementById("RotGear360"      );
      daynight        = evt.target.ownerDocument.getElementById("RotDayNight"     );
      gear5760        = evt.target.ownerDocument.getElementById("RotGear5760"     );
      daymonth        = evt.target.ownerDocument.getElementById("RotDayMonth"     );
      gear11520       = evt.target.ownerDocument.getElementById("RotGear11520"    );
      weekday         = evt.target.ownerDocument.getElementById("RotWeekDay"      );
      gear172800      = evt.target.ownerDocument.getElementById("RotGear172800"   );
      month           = evt.target.ownerDocument.getElementById("RotMonth"        );

      second_hand     = evt.target.ownerDocument.getElementById("RotSecondHand"   );
      minute_hand     = evt.target.ownerDocument.getElementById("RotMinuteHand"   );
      hour_hand       = evt.target.ownerDocument.getElementById("RotHourHand"     );
      daynight_hand   = evt.target.ownerDocument.getElementById("RotDayNightHand" );
      daymonth_hand   = evt.target.ownerDocument.getElementById("RotDayMonthHand" );
      weekday_hand    = evt.target.ownerDocument.getElementById("RotWeekDayHand"  );
      month_hand      = evt.target.ownerDocument.getElementById("RotMonthHand"    );

      Run();
    }

    function Run() {

      // Get computer clock time
      var date  = new Date();
      // var s = date.getSeconds();
      // var m = date.getMinutes();
      var h = date.getHours();    // Get hour so we can check for Summer Time
      var d = date.getDay();      // Week Day, Sunday = 0
      var D = date.getDate();     // Day of month from 1 to 31
      // var M = date.getMonth();    // Month from 0 to 11
      var Y = date.getFullYear();

      var date0 = new Date( Y, 0, 1 ); // Number of milliseconds from 1 January 1970 00:00:00 to start of year
      var milli = date - date0;        // Number of milliseconds since start of year

      // Set start angles... this is a little tricky
      s = Math.floor( milli/1000 );    // Seconds since start of year
      var h2 = Math.floor( (s / 3600) % 24);  // Calculate the hour of the day... not taking into account summer time.
      // alert( 'h=' + h + ' h2=' + h2 );
      if( h != h2 ) {
        s += 3600; // Adjust for summer time
      }

      if( s != old_time ) {
        angle_seconds    =  (s * 6.0                 ) % 360.0;
        angle_gear7_5    = -(s * 6.0 /      7.5      ) % 360.0;
        angle_minutes    =  (s * 6.0 /     60.0      ) % 360.0;
        angle_gear180    = -(s * 6.0 /    180.0      ) % 360.0;
        angle_hours      =  (s * 6.0 /    720.0      ) % 360.0;
        angle_gear360    = -(s * 6.0 /    360.0 + 10 ) % 360.0;
	angle_daynight   =  (s * 6.0 /   1440.0 +  3 ) % 360.0;
        angle_gear5760   = -(s * 6.0 /   5760.0      ) % 360.0;
	angle_daymonth   =  (s * 6.0 /  46080.0      ) % 360.0; // 32 days in month!
        angle_gear11520  = -(s * 6.0 /  11520.0 +  6 ) % 360.0; // Adjustment to align teeth.
        angle_weekday    =  (s * 6.0 /  10080.0 +  6 ) % 360.0; // Adjustment to align teeth.
        angle_gear172800 = -(s * 6.0 / 172800.0 +  1 ) % 360.0;
        angle_month      =  (s * 6.0 / 525600.0      ) % 360.0;

        // Reset day of month to top at start of each month.   
	angle_dm_hand = ( D - 1 + (s % 86400)/86400.0 ) * 360.0/32.0;

        // Set day of week (takes care off offset at beginning of year).
        // Sunday == 0
        angle_wd_hand = ( d + (s % 86400)/86400.0 ) * 360.0/7.0;

        seconds.setAttribute(    "transform", "rotate(" +angle_seconds+   ")");
        gear7_5.setAttribute(    "transform", "rotate(" +angle_gear7_5+   ")");
        minutes.setAttribute(    "transform", "rotate(" +angle_minutes+   ")");
        gear180.setAttribute(    "transform", "rotate(" +angle_gear180+   ")");
        hours.setAttribute(      "transform", "rotate(" +angle_hours+     ")");
        gear360.setAttribute(    "transform", "rotate(" +angle_gear360+   ")");
        daynight.setAttribute(   "transform", "rotate(" +angle_daynight+  ")");
        gear5760.setAttribute(   "transform", "rotate(" +angle_gear5760+  ")");
        daymonth.setAttribute(   "transform", "rotate(" +angle_daymonth+  ")");
        gear11520.setAttribute(  "transform", "rotate(" +angle_gear11520+ ")");
        weekday.setAttribute(    "transform", "rotate(" +angle_weekday+   ")");
        gear172800.setAttribute( "transform", "rotate(" +angle_gear172800+")");
        month.setAttribute(      "transform", "rotate(" +angle_month+     ")");

        second_hand.setAttribute(   "transform", "rotate(" +angle_seconds+  ")");
        minute_hand.setAttribute(   "transform", "rotate(" +angle_minutes+  ")");
        hour_hand.setAttribute(     "transform", "rotate(" +angle_hours+    ")");
        daynight_hand.setAttribute( "transform", "rotate(" +angle_daynight+ ")");
        daymonth_hand.setAttribute( "transform", "rotate(" +angle_dm_hand+  ")");
        weekday_hand.setAttribute(  "transform", "rotate(" +angle_wd_hand+  ")");
        month_hand.setAttribute(    "transform", "rotate(" +angle_month+    ")");

        old_time = s;

      }

      // Repeat every 10th of a second, so the second hand jump is never
      // more than a 10th of a second off.
      setTimeout("Run()", 100)

    }

    window.Run = Run
